#creates a frequency map and shows the most frequent codon
def FrequencyMap(my_dna, k):   
    freq = {}   
    n = len(my_dna)   
    for i in range(n-k+1):   
        pattern = my_dna[i:i+k]   
        if pattern in freq:   
            freq[pattern] += 1   
        else:   
            freq[pattern] = 1   
    return freq   
print(FrequencyMap(my_dna, k))  
def FrequentKmers(my_dna, k):   
    kmers = []   
    freq = FrequencyMap(my_dna, k)   
    m = max(freq.values())   
    for most_frequent_kmer in freq:   
        if freq[most_frequent_kmer] == m:   
             kmers.append(most_frequent_kmer)   
    return kmers   
pattern = FrequentKmers(my_dna, k)
print("most frequent 3-mer is")  
print(pattern)

Pattern = 'ttt'
#defines function PatternMatching for pattern in genome
def PatternMatching(Pattern, my_dna):
    positions = [] # output variable & creates empty list
    #shows the range of how many positions Pattern can be in Genome
    for i in range(len(my_dna)-len(Pattern)+1):
        #creates loop where Pattern is compared, comparator ==, and equal to Genome
        if my_dna[i:i+len(Pattern)] == Pattern:
            #if Pattern is equal to Genome, the positio will be added to the list
            positions.append(i)
    return positions
#defines where the possible codon is from
print(PatternMatching(Pattern, my_dna))

#shows reverse comp
def Reverse(my_dna):
    
    return my_dna[::-1]
    
print(Reverse(my_dna))

def complement(my_dna):
    my_dna = Reverse(my_dna)
    replacement1 = my_dna.replace('A', 't')
    replacement2 = replacement1.replace('T', 'a')
    replacement3 = replacement2.replace('C', 'g')
    replacement4 = replacement3.replace('G', 'c')
    return replacement4.upper()

print(complement(my_dna))

c = complement(my_dna)

#shows RNA
def RNAcomp(my_dna):
    replacement5 = c.replace("T", "U")
    return replacement5
print(RNAcomp(my_dna))
rna = RNAcomp(my_dna)
      

bases = ['U', 'C', 'A', 'G']
codons = [a+b+c for a in bases for b in bases for c in bases]
amino_acids = 'FFLLSSSSYY**CC*WLLLLPPPPHHQQRRRRIIIMTTTTNNKKSSRRVVVVAAAADDEEGGGG'
codon_table = dict(zip(codons, amino_acids))

#shows Protein     
def translate_rna(rna):
    start = rna.find('AUG')
    peptide = []
    i = start
    while i < len(rna)-2:
        codon = rna[i:i+3]
        a = codon_table[codon]
        if a == '*':
            break
        i += 3
        peptide.append(a)
    return ''.join(peptide)

print(translate_rna(rna))

